                                pagina: pagina,
                                destino: "[contenedor=reservasDelCliente]",
                                columnasGrid: columnasGrid,
                                gridUID: "grisReservasDelCliente",
                                mascaraURL: {
                                    mascara: "/administracion/reservas/reserva:",
                                    parametro: "reservaUID"
                                },
